import { Camera, CameraResultType } from "@capacitor/core";
import { IonButton, IonImg } from "@ionic/react"
import { useDispatch, useSelector } from "react-redux";
import { selectImage } from '../../../redux/actions/formGasto';


const Comprobante: React.FC = () => { 
    const { imgComp='' } = useSelector((state: any) => state.formGasto);
    const dispatch = useDispatch();

    const  takePicture  = async ():Promise<any> =>  {
        const _image = await Camera.getPhoto({
            quality: 10,
            allowEditing: false,
            resultType: CameraResultType.DataUrl,
            height: 160,
            width: 400,

        });
            console.log(_image);

        const imageUrl = _image.dataUrl||'';
        dispatch(selectImage(imageUrl));
    }

    return <>
        <IonImg src={imgComp || 'https://previews.123rf.com/images/mehdialigol/mehdialigol1709/mehdialigol170900046/86870202-icon-photo-camera-in-simple-line-vector-image.jpg'} /> 
        <IonButton slot='end' color='tertiary' onClick={takePicture}>
            Buscar Imagen 
        </IonButton>
    </>;
}

export default Comprobante;
