import { IonCard, IonCardHeader, IonCardTitle, IonIcon, IonInput, IonItem, IonLabel, IonList, IonTextarea } from "@ionic/react";
import { reader, wallet } from 'ionicons/icons';
import { useDispatch, useSelector } from "react-redux";

const Descripcion = () => { 
    const { descripcion='',costo=0 } = useSelector((state: any) => state.formGasto);
    const dispatch = useDispatch();

    return (
        <IonList>
            <br />
            <IonCard>
            <IonItem>
                <IonIcon icon={wallet} slot='start' />
                <IonLabel position='fixed'>gasto $ </IonLabel>
                <IonInput type='number' min='0'  value={costo} />
            </IonItem>
            </IonCard>
            <br/>
            <IonCard>
                <IonItem >
                    <IonIcon slot='start' icon={reader}/>
                <IonCardTitle > Descripcion</IonCardTitle>
                </IonItem>
                <IonTextarea rows={6} maxlength={150} value={descripcion} />
            </IonCard>
        </IonList>
    );
}

export default Descripcion;
