import { IonLabel, IonSegment, IonSegmentButton, IonTitle, IonToolbar } from "@ionic/react";
import { useState } from "react";
import Layout from "../../components/Layout/Layout";

const opciones:string[] = ['Tipo','Descripcion','Comprobante','Resumen'];

const Gastos: React.FC = () => { 
    const [selected, onSelected] = useState('Tipo');

    const onchange = (event:any) => { 
            console.log(event.detail);            
    }

    const Selecciones = (name:string) => <IonSegment disabled={selected != name} value={name} onIonChange={onchange}>
        <IonSegmentButton>
            <IonLabel>{name}</IonLabel>
        </IonSegmentButton>
    </IonSegment>;

    return <Layout name='Gastos'>
        <div style={{
            height:80
        }}>
        {
            opciones.map((value, index, array) => {
                return Selecciones(value);
            })
        }
        </div>
    </Layout>
}

export default Gastos;
